                    { 9627, "", "2 min per ep", new DateTime(2010, 12, 2, 16, 0, 0, 0, DateTimeKind.Local), 5, "https://cdn.myanimelist.net/images/anime/11/26662.jpg", 12069, "PG-13 - Teens 13 or older", 5.46m, 3, 1, new DateTime(2010, 11, 4, 17, 0, 0, 0, DateTimeKind.Local), 1, "Shorts run during Shueisha's Saki-yomi Jum-Bang! television program starting on November 5, 2010. \n\n(Source: AniDB)", "Kakko Kawaii Sengen!", null, "カッコカワイイ宣言！", "https://www.youtube.com/watch?v=zT5lFf7SnZ8", 1, "https://myanimelist.net/anime/9627/Kakko_Kawaii_Sengen" },
