                    { 35306, "", "2 min per ep", new DateTime(2014, 3, 31, 17, 0, 0, 0, DateTimeKind.Local), 3, "https://cdn.myanimelist.net/images/anime/12/85029.jpg", 19952, "G - All Ages", null, null, 1, new DateTime(2013, 10, 31, 17, 0, 0, 0, DateTimeKind.Local), 1, "A collaboration between Kamiusagi Rope and Nipponham's Mori no Kaori product line. Meant to showcase sausages, bacon, ham, etc. that have a rustic  forest flavor with no added colors and reduced sodium. Episodes were available to view by entering a code online that was given on the inner label of a Mori no Kaori's product package.", "Mori no Kaori x Kamiusagi Rope", null, "森の薫り×紙兎ロペ", null, 6, "https://myanimelist.net/anime/35306/Mori_no_Kaori_x_Kamiusagi_Rope" },
                    { 35307, "", "3 min per ep", new DateTime(2014, 6, 20, 17, 0, 0, 0, DateTimeKind.Local), 2, "https://cdn.myanimelist.net/images/anime/3/85030.jpg", 13970, "G - All Ages", null, null, 1, new DateTime(2014, 1, 20, 16, 0, 0, 0, DateTimeKind.Local), 1, "A collaboration between Kamiusagi Rope and two of Shiseido's product lines: Anessa and Fullmake Washable Base (FWB). These episodes were included on the 6th DVD of Kamiusagi Rope: Warau Asa ni wa Fukuraitaru tte Maji ssuka!? as episode 23 and 24 released on March 18, 2015.", "Shiseido x Kamiusagi Rope", null, "資生堂×紙兎ロペ", null, 6, "https://myanimelist.net/anime/35307/Shiseido_x_Kamiusagi_Rope" },
                    { 35308, "", "2 min per ep", null, 3, "https://cdn.myanimelist.net/images/anime/6/85031.jpg", 19372, "G - All Ages", null, null, 1, new DateTime(2014, 11, 30, 16, 0, 0, 0, DateTimeKind.Local), 1, "A collaboration between Kamiusagi Rope and Lion Corporation, a toiletry company for their end-of-the-year toothbrush replacement campaign.", "Lion x Kamiusagi Rope", null, "ライオン×紙兎ロペ", null, 6, "https://myanimelist.net/anime/35308/Lion_x_Kamiusagi_Rope" },
                    { 35309, "", "1 min per ep", new DateTime(2017, 11, 1, 17, 0, 0, 0, DateTimeKind.Local), 7, "https://cdn.myanimelist.net/images/anime/12/85032.jpg", 19332, "G - All Ages", null, null, 1, new DateTime(2016, 10, 9, 17, 0, 0, 0, DateTimeKind.Local), 1, "A collaboration between Kamiusagi Rope and J-League's Levain Cup soccer tournament.", "Levain Cup x Kamiusagi Rope", null, "ルヴァンカップ×紙兎ロペ", null, 6, "https://myanimelist.net/anime/35309/Levain_Cup_x_Kamiusagi_Rope" },
