                    { 39332, "", "1 hr 23 min per ep", new DateTime(2005, 12, 27, 16, 0, 0, 0, DateTimeKind.Local), 2, "https://cdn.myanimelist.net/images/anime/1226/99268.jpg", 6747, "R+ - Mild Nudity", 6.54m, null, 2, new DateTime(2005, 12, 26, 16, 0, 0, 0, DateTimeKind.Local), 1, "Special recap edition of Monster TV series in 2 parts.", "Monster Special Edition", null, "モンスター スペシャルバージョン", null, 5, "https://myanimelist.net/anime/39332/Monster_Special_Edition" },
                    { 39358, "", "15 min", null, 1, "https://cdn.myanimelist.net/images/anime/1058/99377.jpg", 14464, "G - All Ages", null, null, 2, new DateTime(1992, 12, 31, 16, 0, 0, 0, DateTimeKind.Local), 1, "A traffic safety film starring the cast of Tetsuwan Atom. Its purpose educate elementary school children that the leading cause of death involving children and automobiles, is that the children are jumping into the road when they're not supposed to be. Meaning that children using crosswalks lawfully or children inside vehicles that crash, are very unlikely to die, and that children themselves must protect themselves over something easily avoidable and obey traffic laws.", "Tetsuwan Atom no Koutsuu Anzen", null, "鉄腕アトムの交通安全", null, 3, "https://myanimelist.net/anime/39358/Tetsuwan_Atom_no_Koutsuu_Anzen" },
                    { 39360, "", "4 min", null, 1, "https://cdn.myanimelist.net/images/anime/1801/121536.jpg", 11193, "R - 17+ (violence & profanity)", 5.71m, null, 2, new DateTime(1999, 7, 19, 17, 0, 0, 0, DateTimeKind.Local), 1, "The Phoenix asks us why humans choose war as a means of solving problems, while it looks back on the history of Kyoto and ponders on the riot at Ikeda-ya by the Shinsengumi in the last days of the Tokugawa regime.\n\nThe short film was also included on the Osamu Tezuka Sakuhin-shuu: Kyoto Animation Theater-hen DVD released in 2015.\n\n(Source: Official Site)", "Shinsengumi", "Shinsen-gumi", "新撰組", null, 2, "https://myanimelist.net/anime/39360/Shinsengumi" },
