                    { 7270, "", "1 hr 39 min per ep", new DateTime(2010, 2, 22, 16, 0, 0, 0, DateTimeKind.Local), 3, "https://cdn.myanimelist.net/images/anime/11/17217.jpg", 1765, "PG-13 - Teens 13 or older", 7.54m, null, 1, new DateTime(2009, 10, 26, 17, 0, 0, 0, DateTimeKind.Local), 1, "Condensed version of both the first and second seasons of Gundam 00 featuring some new animated sequences and some partially re-recorded dialogue.", "Kidou Senshi Gundam 00 Special Edition", "Mobile Suit Gundam 00 Special Edition", "機動戦士ガンダム00 スペシャルエディション", null, 3, "https://myanimelist.net/anime/7270/Kidou_Senshi_Gundam_00_Special_Edition" },
                    { 7290, "", "1 hr", null, 1, "https://cdn.myanimelist.net/images/anime/9/76036.jpg", 10064, "PG - Children", 5.95m, null, 8, new DateTime(1967, 3, 18, 16, 0, 0, 0, DateTimeKind.Local), 1, "The evil organization 'Black Ghost' attacked around the world, using dinosaur (plesiosaur) type giant robots, and the team of 9 cyborgs launched their quest for the Black Ghost's hideout. 009 meets a girl named Helena...\n\n(Source: upp.so-net.ne.jp)", "Cyborg 009: Kaijuu Sensou", "Cyborg 009 and the Monster Wars", "サイボーグ００９ 怪獣戦争", "https://www.youtube.com/watch?v=B0hIV93ke8g", 2, "https://myanimelist.net/anime/7290/Cyborg_009__Kaijuu_Sensou" },
